import WeatherApi

struct WeatherHour {
    var icon: String?
    var hour: String?
    var temp: String?
}
enum WeatherDetail {
    enum Success {
        struct Response {
            var weatherData: OpenWeatherEntity
        }
        struct ViewModel {
            var icon: String?
            var description: String?
            var temp: String?
            var sunrise: String?
            var sunset: String?
            var feelslike: String?
            var pressure: String?
            var humidity: String?
            var visibility: String?
            var winspeed: String?
            var hours: [WeatherHour]
        }
    }
    enum Failure {
        struct Response {
            var error: Error
        }
        struct ViewModel {
            var error: String
        }
    }
}

